int memoization(string& s,string& d,int sin,
                   int din,vector<vector<int>>& memory)
    {
        if(din==0)
            return sin;
        if(sin==0)
            return din;
        if(memory[sin][din]!=-1)
            return memory[sin][din];
        if(s[sin-1]==d[din-1])
            return memory[sin][din]=
            memoization(s,d,sin-1,din-1,memory);
        return memory[sin][din]=
            1+min(memoization(s,d,sin-1,din,memory),
                  min(
                 memoization(s,d,sin-1,din-1,memory),
                 memoization(s,d,sin,din-1,memory)));
    }
