            return memory[sin][din]=
            memoization(s,d,sin-1,din-1,memory);
        return memory[sin][din]=
            1+min(memoization(s,d,sin-1,din,memory),
                  min(
                 memoization(s,d,sin-1,din-1,memory),
                 memoization(s,d,sin,din-1,memory)));
    }
