func minDistance(_ word1: String, _ word2: String) -> Int {
	var table = Array(repeating: Array(repeating: 0, count: word1.count + 1), count: word2.count + 1)

	// adding the empty string state
	let word1Arr = Array(" " + word1)
	let word2Arr = Array(" " + word2)

	// Table setup for the empty string state
	for row in 0..<table.count {
		table[row][0] = row
	}
	for col in 0..<table[0].count {
		table[0][col] = col
	}

	// Begin iterating at index 1 to word2.count - this is the first letter of word2
	for i in 1..<table.count {

		for j in 1..<table[i].count {

			let insert: Int = table[i-1][j]
			let remove: Int = table[i][j-1]
			let replace: Int = table[i-1][j-1]

			if word2Arr[i] == word1Arr[j] {
				table[i][j] = replace
			} else {
				table[i][j] = min(insert, remove, replace) + 1
			}
		}
	}

	// Return the value at the bottom right
	return table[word2.count][word1.count]
}
```

