                    // if we have known the min distance bwtween s1[1...r-1] and s2[1...c]
					// we can  delete s1[r] first then convert s1[1...r-1] to s2[1...c]
                    int del = lm[r - 1][c] + 1;
                    // if we have known the min distance between s1[1...r] and s2[1...c-1]
					// we can convert s1[1...r] to s2[1...c-1] then insert s2[c] to the end of s1
                    int insert = lm[r][c - 1] + 1;
                    // find the min value among the above operation steps
                    int result = min(replace, del);
                    result = min(result, insert);
                    lm[r][c] = result;
                }
            }
        }
        return lm[size1][size2];
    }
