class Solution {
public:
    int trailingZeroes(int n) {  

	//we just have to count number of 5's as  (5*2) contributes to 1 zero, 
	//as number of 2's are always greater than number of 5's, 
	//count of 5's are the limiting case for  trailing 0's


      if(n <= 4){                   //if n is less than 4 then leading zeroes are 0
        return 0;
      }
                                         //considering powers of  5 as 25 contributes to 2 --> 5's, 
	                                     //125 contrinutes to 3 --> 5's similarly 625 contributes 4  --> 5's and so on ,
	                                     //and dividing then gives the count of 5's for the value of n
	   int countof5 = 0;   

      for(int i = 1; i<=5 ;++i){                   
        int temp_count=n/(pow(5,i));  

        if(temp_count==0){
          break;
        }

        else{
          countof5+=temp_count;
        }

      }

      return  countof5;
    }
};


