class Solution:
    def myPow(self, x: float, n: int) -> float:
        def pow(x, n):
            if n == 0:
                return 1
            elif n%2==0:
                res =  pow(x, n/2)
                return res*res
            else:
                res =  pow(x, (n-1)/2)
                return x*(res**2)

        if n>=0: 
            return pow(x, n)
        else:
            return 1/pow(x, abs(n))

