/**
 * @brief A function to calculate a^b % k
*/
int powModFunc(int a, int b, int k)
{
	// Overflow occured if directly calculate a^b % k
	// Use (a*a*a*a*...) % k instead
	// let a^1 = kq + r,      a^1 % k = r % k                         =             (a % k)     
	//     a^2 = akq + ar,    a^2 % k = ar % k  = (a % k)(r % k) % k  = (a^1 % k)   (a % k) % k
	//     a^3 = aakq + aar,  a^3 % k = aar % k = (a % k)(ar % k) % k = (a^2 % k)   (a % k) % k
	// So                     a^n % k                                 = (a^n-1 % k) (a % k) % k
	// Notes that a^0 = 1

	if (b == 0) return 1;

	long reminderA = a % k;
	long result = reminderA;
	for (int i = 1; i < b ; i++)
	{
		result = (result * reminderA) % k;
	}        
	return result;
}

int superPow(int a, vector<int>& b) {
	int k = 1337;
	// There have two properties we must know.
	// 1. ab % k = (a%k)(b%k)%k
	// 2. a^(bc) % k = (a^b % k)^c % k
	// e.g. a^123 % k = (a^120 * a^3) % k
	//                = ((a^12 * 10) % k) (a^3 % k) % k                           // Use property 1
	//                = ((a^12 % k)^10 % k) (a^3 % k) % k                         // Use property 2
	//                = (((a^1 % k) (a^2 % k) % k)^10 % k) (a^3 % k) % k          // Use property 1

	int result;
	if (b.size() == 1) {
		result = powModFunc(a, b[0], k);
	}
	else
	{
		int lastValue = b.back();
		b.pop_back();
		result = ((long)powModFunc(superPow(a, b), 10, k) * (long)powModFunc(a, lastValue, k)) % k;
	}
	return result;
}
```

