class Solution {
    int m=1337;
    //calculate power with binary expo
    int power(int a,int b){
        int res=1;
        while(b){
            if(b%2){
                res=(res*a)%m;
                --b;
            }
            else{
                a=(a*a)%m;
                b=b>>1;
            }
        }
        return res%m;
    }
public:
    int superPow(int a, vector<int>& b) {
        //  (a^x)%m --> ( (a%m)^(x%f(m)) )%m; --> euler algo
        //  f(m)--> total number of coprimes
        // if gcd(p,q)==1 then p and q is coprime
        // first of all find all coprimes of m
        // to find coprimes-->
        // i.e f(m)=m*(1-1/p1)*(1-1/p2)*(1-1/p3)...
        // p1,p2,p3,... are unique prime factors of m

        // In given question m=1337
        // primes factors of m is 7,191
        // so 1337*(1-1/7)*(1-1/191)=>1140 (total number of coprime)

        a=a%m;
        int x=0;
        for(int i=0;i<b.size();i++){
            x=(x*10+b[i])%(1140);
        }
        return power(a,x);
    }
};
