class Solution {
public:
    typedef long long LL;
    int superPow(int a, vector<int>& b) {
        a %= 1337;
        if(b.empty()) return 1;
        int t = b.back();
        b.pop_back();
        return qmi(a, t) * qmi(superPow(a, b), 10) % 1337;
    }

    LL qmi(LL a, LL n)
    {
        LL res = 1;
        while(n)
        {
            if(n & 1)
                res = res * a % 1337;
            n>>=1;
            a = a * a % 1337 ;
        }
        return res;
    }
};
