	int superPow(int a, int* b, int bSize){

    if(bSize == 0)
        return 1;

    if(b[bSize-1]%2 == 1){
        b[bSize-1]--;
        return ( (a % 1337) * superPow(a, b, bSize) ) % 1337;
    }

    half(b, bSize);

    if(b[0]==0)
        b++, bSize--;

    return superPow( (a % 1337) * (a % 1337), b, bSize);
	}

