```
class Solution {
public:
    int superPow(int a, vector<int>& b) {
        // 1337 = 7 * 191 -> 1140 = 6 * 190
        // a^phi(n) = 1 mod n
        // a^1140 = 1 mod 1337
        // a^b mod 1337 = a^(b % 1140) % 1337 = (a % 1337)^(b % 1140) % 1337
        //   because a = (d + 1137f)^b mod n = d^b mod n

        auto op = [] (const auto &cur, const auto &in) {
            return (cur * 10 + in) % 1140;
        };
        int e = accumulate(b.begin(), b.end(), 0, op);

        // cout << a % 1337 <<", "<<e % 1140<<endl;
		// Here we'd like to lower the exponent. If a = 932, b = 200:
		// 932^200 % 1337
		// = (932^2 % 1337) ^ 100 % 1337
		// = (911^2 % 1337) ^ 50 % 1337
		// = (981^2 % 1337) ^ 25 % 1337
		// = 1058 * (1058^2 % 1337) ^ 12 % 1337
		//   ^^^^ this value will be cached in `rem`
		// ...
		// = rem * <value> ^ 1 % 1337
        a =  a % 1337;
        int rem = 1;
        while (e > 1) {
            if (e & 1) {
                rem = rem * a % 1337;
                --e;
            }
            a = a * a % 1337;
            e >>= 1;
        }
        return rem * a % 1337;
    }
};
```

