class Mod{

  private final long mod;

  Mod(int mod) {
    this.mod = mod;
  }

  int get(long val) {

    return (int)((val + mod) % mod);

  }

  int mul(long x, long y) {

    return get(get(x) * get(y));

  }

  int add(long x, long y) {
    return get(get(x) + get(y));
  }

  public int expo(long a, long b) {

    long res = 1;

    while (b > 0) {

      if (b % 2 != 0) res = mul(res, a);

      a = mul(a, a);

      b /= 2;

    }

    return get(res);

  }

  public int reduce(int b[]) {

    int res = 0, n = b.length;

    for (int i = 0; i < b.length; i++) {

      res = add(res, mul(b[i], expo(10, n - i - 1)));

    }

    return res;

  }

}

class Solution {

  public int superPow(int a, int[] b) {

    Mod mod = new Mod(1337);

    Mod tmod = new Mod(1140);

    return mod.expo(a,tmod.reduce(b));

  }
}
