class Solution {
    int temp=1337, m=1337;
    void sieve() {
        bool isPrime[m+1];
        memset(isPrime, 1, sizeof(isPrime));
        for(int p=2; p*p<=m; p++) {
            if(isPrime[p]) {
                for(int i=p*p; i<=m; i+=p) isPrime[i]=0;
            }
        }
        for(int i=2; i<m+1; i++) {
            if(isPrime[i]&&m%i==0) temp*=(i-1), temp/=i;
        }
        // cout<<temp;
    }

    int power(int a, int b) {
        a%=m;
        int ans=1;
        long long power=1;
        while(b) {
            if(power&b) {
                ans=(ans%m*a)%m;
                b-=power;
            }
            a=(a*a)%m;
            power<<=1;
        }
        return ans%m;
    }
public:
    int superPow(int a, vector<int>& b) {
        sieve();
        int num=0, base=1;
        for(int i=b.size()-1; i>=0; i--) {
            num+=(b[i]%temp*(base%temp))%temp;
            base=(base*10)%temp;
        }
        return (power(a,num))%m;
    }
};
