class Solution {
    public int superPow(int a, int[] b) {
        List<Integer> modulos = getModulos(a, 1337);
        int remainder = calcRemainder(b, modulos.size());
        int moduloIndex = (remainder == 0 ? modulos.size() : remainder) - 1;
        return modulos.get(moduloIndex);
    }

    public List<Integer> getModulos(int pow, int mod) {
        List<Integer> modulos = new ArrayList<Integer>();
        Set<Integer> found = new HashSet<Integer>();
        int modulo = pow % mod;
        do {
            found.add(modulo);
            modulos.add(modulo);
            modulo *= (pow % mod);
            modulo %= mod;
        } while (!found.contains(modulo));
        return modulos;
    }

    public int calcRemainder(int[] dividend, int divisor) {
        int remainder = 0;
        for (int d : dividend) {
            remainder *= 10;
            remainder += d;
            remainder %= divisor;
        }
        return remainder;
    }
}

