// Approach: Use Euler formula but write a custom function to divide array by 2
class Solution {
    // divide number by 2
    void div2(vector<int> &b){
        int carry=0;
        for (int i=0;i<b.size();i++){
            int num=carry*10+b[i];
            int q=num/2;
            int rem=num%2;
            b[i]=q;
            carry=rem;
        }
        while (!b.empty() and b.front()==0){
            b.erase(b.begin());
        }
    }
    int expo (int n, vector<int>& b, int m){
        int ans=1;
        while (!b.empty()){
            if (b.back()&1) ans = ((ans%m)*(n%m))%m;
            n=((n%m)*(n%m))%m;
            div2(b);
        }
        return ans;

    }
public:
    int superPow(int a, vector<int>& b) {
        return expo(a,b,1337);
    }
};
