class Solution {
    public int superPow(int a, int[] b) {
        int mod = 1337;
        a %= mod; // Reduce a to the range [0, 1336] to optimize
        int num = 0;
        for (int i = 0; i < b.length; i++) {
            num = (num * 10 + b[i]) % 1140;
        }
        if (num == 0) {
            num += 1140;
        }
        int result = 1;
        for (int i = 0; i < num; i++) {
            result = (result * a) % mod;
        }
        return result;
    }
}

