class Solution {
public:

    int pow(int x, int n)
    {
        x %= 1337;
        if (x == 0) return 0;
        int a = 1;
        while (n) {
            if (n & 1) {
                a *= x;
                a %= 1337;
            } 
            x *= x;
            x %= 1337;
            n /= 2;
        }
        return a;
    }

    int superPow(int a, vector<int>& b) {
        const int phi1337 = 1140;
        int pw = 0;
        for (int i = 0; i < b.size(); i++) {
            pw = pw*10 + b[i];
            pw %= phi1337;
        }
        return pow(a, pw);
    }
};
