class Solution {
public:
    int superPow(int a, vector<int>& b) {
        long long num=0;
        for(int i=0;i<b.size();i++) //calculate num
        {
            num=(num*10+b[i])%1140; 
        }
        a=a%1337;
        long long res=1;
        for(int i=0;i<num;i++)  //calculate a^nums
            res=1LL*(res*a)%1337;
        return res;
    }
};
```

