class Solution {
public:
int base = 1337;

int mypow(int a, int k) {

    a %= base;
    int res = 1;
    for (int _ = 0; _ < k; _++) {

        res *= a;

        res %= base;
    }
    return res;
}

int superPow(int a, vector<int>& b) {
    if (b.empty()) return 1;
    int last = b.back();
    b.pop_back();

    int part1 = mypow(a, last);
    int part2 = mypow(superPow(a, b), 10);

    return (part1 * part2) % base;
}
};
```

