    private boolean check(String word,Set<String> dictionary) {
        if(dictionary.contains(word)) return true;
        int i = word.length() - 1;
        while(i >= 1) {
            if(dictionary.contains(word.substring(0,i)) && check(word.substring(i),dictionary)) return true;
            i--;
        }
        return false;
