        for (int i = start; i < word.size(); ++i)
        {
            TrieNode & cur_node = *cur_node_ptr;
            if (cur_node[Trie::ENDC] != nullptr && isConcatenated(word, i))
                return true;
            cur_node_ptr = cur_node[word[i]];
            if (cur_node_ptr == nullptr)
                return false;
        }
        if ((*cur_node_ptr)[Trie::ENDC] != nullptr)
            return true;
        return false;
