        sort(words.begin(), words.end(), [](const string &lhs, const string &rhs){return lhs.size() < rhs.size();});
        for (auto word : words)
        {
            if (word.empty())
                continue;
            if (isConcatenated(word, 0))
                results.push_back(word);
            trie.insert(word);
        }
        return results;
