        for(auto x:words)
            m[x]++;
        vector<string> ans;
        for(auto x:words)
        {
            bool flag=cal(x,0,0);
            // if given string satisfy the condition then add it to our answer
            if(flag)
                ans.push_back(x);
        }
        return ans;
    }
};
