    struct Node {
        Node():end(),nxt() {};
        bool end;
        Node* nxt[26];
    };
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        vector<string> res;
        sort(words.begin(),words.end(),comp());
        Node *root = new Node();
        for(auto &s:words) {
            if(s.empty()) continue;
            if(canbrk(0,s,root)) res.push_back(s);
            addWord(s,root);
        }
        destroy(root);
        return res;
    }
    bool canbrk(int p, string &s, Node *r) {
        int n = s.size();
        if(p==n) return 1;
        string sub;
        for(int i=p;i<n;i++) if(search(sub+=s[i], r)&&canbrk(i+1,s,r)) return 1;
        return 0;
    }
    void addWord(string word, Node *p) {
        for(char c:word) {
            Node *&nxt = p->nxt[c-'a'];
            if(!nxt) nxt = new Node();
            p = nxt;
        }
        p->end = 1;
    }
    bool search(string& word, Node *r) {
        for(char c:word) {
            Node *&nxt = r->nxt[c-'a'];
            if(!nxt) return 0;
            r = nxt;
        }
        return r->end;
    }
    void destroy(Node* r) {
        if (!r) return;
        for (int i = 0; i < 26; i++) destroy(r->nxt[i]);
        delete r;
    }
