            marker = root
            for i in range(start, n):
                marker = marker.children[word[i]]
                if marker.isEnd:           # smaller word encountered
                    if i == n-1:               # leaf node
                        return count>=1
                    elif dfs(word, i+1, root, count+1):     # increment the count and start a new DFS
                        return True
            return False

