        sort(words.begin(), words.end(), [](const string& w1, const string& w2) {
            return w1.size() < w2.size();
        });

        vector<string> ret;
        root = new Trie();

        for(auto &w: words)
        {
            if (w.empty()) continue;
            if(dfs(root, root, w, 0, 0)) 
                ret.push_back(w);
            else 
                insert(w);
        }

        return ret;
    }
};
