class Solution {
public:

    //Our lc function breaks the word into two parts left and right
    // and check if these are already present in our dictionary 
    //or can be formed using the words presnt in dictionary

    bool lc(string s , set<string>&dictionary){

        for(int i=1; i<s.size(); ++i){

            string left=s.substr(0,i);
            string right=s.substr(i);

            //iterators for the string to check their presence
            auto ii=dictionary.find(left);
            auto j=dictionary.find(right);

            // if left is found then right is also found 
            //or our right can be made

            if(ii!=dictionary.end()){
                if(j!=dictionary.end() or lc(right,dictionary)) return true;
            }
        }

        return false;
    }
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {

        //Made for storing the answer
        vector<string> beri;

        //Making a set of the string (dictionary) for all the words 
        set<string> dictionary;
        for(auto i:words) dictionary.insert(i);

        //we are checking if the particular word can be made
        // from other words presnt inside the dictionary
        for(auto i :words){
            if(lc(i,dictionary)) beri.push_back(i);
        }

        return beri;
    }
};
