    def exists(self, word):
        node = self.root
        for i, c in enumerate(word):
            if c in node.children:
                node = node.children[c]
                if node.isWord:
                    if i == len(word)-1 or self.exists(word[i+1:]):
                        return True
                    else:
                        continue
            else:
                return False

