        trie *cur=root;
        for(int i=0;i<s.length();i++){
            if(!cur->child[s[i]-'a'])cur->child[s[i]-'a']=getnode();
            cur=cur->child[s[i]-'a'];
        }
        cur->isend=true;
    }
