public List<String> findAllConcatenatedWordsInADict(String[] words) {
        TrieNode root = buildTrie(words);
        List<String> result = new ArrayList();
        for(String word : words){
            if(searchWords(root, word, 0, 0))
                result.add(word);
        }
        return result;
    }

    private boolean searchWords(TrieNode root, String word, int idx, int count){

        int n = word.length();
        TrieNode node = root;
        for(int i=idx ; i<n ; i++){
            char c = word.charAt(i);
            node = node.next[c - 'a'];
            if(node == null) return false;

            if(node.isWord){
                //check at the end
                if(i == n-1){
                    //check at least this word is made of more than 1 word
                    return count >= 1;
                } else{
                    if(searchWords(root, word, i+1, count+1))
                        return true;
                }
            }

        }
        return false;
    }

    private TrieNode buildTrie(String[] words){
        TrieNode root = new TrieNode();
        for(String word : words){
            if(word.isEmpty()) continue;
            TrieNode node = root;
            for(int i=0 ; i<word.length() ; i++){
                char c = word.charAt(i);
                if(node.next[c - 'a'] == null){
                    node.next[c - 'a'] = new TrieNode();
                }
                node = node.next[c - 'a'];
            }
            node.isWord = true;
        }
        return root;
    }

    class TrieNode{
        TrieNode[] next = new TrieNode[26];
        boolean isWord;
    }

