class TrieNode {
public: 
    TrieNode* children[26] = {nullptr}; 
    bool word = false; 
    ~TrieNode() {
        for (auto& child : children) 
            delete child; 
    }
}; 

class Trie {
    TrieNode* root = nullptr; 
public: 
    Trie() { root = new TrieNode(); }

    ~Trie() { delete root; }

    void insert(string word) {
        TrieNode* node = root; 
        for (int i = word.size()-1; i >= 0; --i) {
            if (!node->children[word[i] - 'a'])
                node->children[word[i] - 'a'] = new TrieNode(); 
            node = node->children[word[i] - 'a']; 
        }
        node->word = true; 
    }

    bool search(string word) {
        vector<bool> dp(1 + word.size()); 
        dp[0] = true; 
        for (int i = 0; i < word.size(); ++i) {
            TrieNode* node = root; 
            for (int j = i; j >= 0; --j) {
                if (!node->children[word[j] - 'a']) break; 
                node = node->children[word[j] - 'a']; 
                if (dp[j] && node->word) {
                    dp[i+1] = true; 
                    break; 
                }
            }
        }
        return dp.back(); 
    }
}; 


class Solution {
public:
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        sort(words.begin(), words.end(), [](const string& w1, const string& w2) { return (w1.size() < w2.size()); });
        vector<string> ans;

        Trie* trie = new Trie(); 
        for (auto& word : words) 
            if (word.size()) {
                if (trie->search(word)) ans.push_back(word); 
                trie->insert(word); 
            }

        delete trie; 
        return ans;
    }
};
