public List<String> findAllConcatenatedWordsInADict(String[] words) {

	List<String> res = new ArrayList<String>();
	if (words.length == 1)
		return res;

	Arrays.sort(words, new Comparator<String>() {
		@Override
		public int compare(String o1, String o2) {
			return o1.length() - o2.length();
		}
	});

	HashSet<String> dict = new HashSet<String>();
	for (String word : words) {
		if (word.length() != 0 && wordBreak(word, dict))
			res.add(word);
		dict.add(word);
	}

	return res;
}

// O(s.length^2)
public boolean wordBreak(String s, HashSet<String> dict) {
	int[] memo = new int[s.length()];
	int len = s.length();
	return wordBreakDFS(len - 1, s, dict, memo);
}

// O(s.length^2)
public boolean wordBreakDFS(int start, String s, HashSet<String> dict, int[] memo) {

	if (start == -1)
		return true;

	if (memo[start] == 1)
		return false;

	boolean ans = false;
	for (int i = start; i >= 0; i--) {
		if (dict.contains(s.substring(i, start + 1))) {
			ans = wordBreakDFS(i - 1, s, dict, memo);
			if (ans)
				return true;
		}
	}
	memo[start] = 1;
	return false;
}

