      int counter = 0;

      TrieNode node = root;

      for (int i = index; i < word.length(); i++) {
        counter++;

        char ch = word.charAt(i);

        if (!node.map.containsKey(ch)) {
          return false;
        }

        node = node.map.get(ch);
        if (node.isWord) {
          if (i == word.length() - 1 && counter < word.length()) {
            return true;
          } else if (i < word.length() - 1 && isConcatenated(root, word, i + 1)) {
            return true;
          }
        }
      }

      return false;
    }

