        for (int i = 0; i < word.length(); i++) {
          char ch = word.charAt(i);

          if (node.map.containsKey(ch)) {
            node = node.map.get(ch);
          } else {
            node.map.put(ch, new TrieNode());
            node = node.map.get(ch);
          }
        }

        node.isWord = true;
      }
    }

