class Solution {
public:
    unordered_set<string> mp;
    // dp[i] = number of ways to partition strings such that each substring is in the words;
    int rec(string &s,int n,int i,vector<int> &dp){
        if(i>=n) return true;
        if(dp[i]!=-1) return dp[i];
        dp[i] = 0;
        for(int j=i;j<n;j++){
            string tmp = s.substr(i,j-i+1);
            if(mp.count(tmp)){ // if substring is in words.
                dp[i] += rec(s,n,j+1,dp);
            }
        }
        return dp[i];
    }
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words){
        mp.clear();
        vector<string> ans;
        for(auto &ele:words) mp.insert(ele);
        for(auto &ele:words){
            vector<int> dp(ele.size()+1,-1);
            int ways = rec(ele,ele.size(),0,dp);
            if(ways>1){ // we can always partition whole string so that way is not considered.
                ans.push_back(ele);
            }
        }
        return ans;
    }
};
```

