                return True

            subword = ""
            for i in range(len(word)):
                subword += word[i] # append char to the end of the subword
                if subword in self.words_dict:
                    if is_concatenated(word[i + 1:], count + 1):
                        return True

            # nothing found at this point
            return False

        # construct a dict of words to enable O(1) lookup
        self.words_dict = dict()
        for w in words:
            self.words_dict[w] = True

        res = []
        for w in words:
            if is_concatenated(w, 0):
                res.append(w)

        return res
