class Solution:
    def findAllConcatenatedWordsInADict(self, words: List[str]) -> List[str]:


        def check(word):
            q=deque([(0,[],set([0]))])

            while q:
                start,path,seen=q.popleft()
                if start==len(word) :return True

                for end in range(start+1,len(word)+1):
                    newWord=word[start:end]
                    if newWord in words and newWord!=word and end not in seen:
                        seen.add(end)
                        q.append((end,path+[newWord],seen))
            return False


        words=set(words)

        res=[]
        for word in words:
            if check(word) and word:
                res.append(word)
        return res

