class Solution {
    //Caching
    HashMap<String,Boolean> map = new HashMap<String,Boolean>();
    public List<String> findAllConcatenatedWordsInADict(String[] words) {

     HashSet<String> set =  new HashSet<>();
     List<String> result =  new ArrayList<>();

     for(String word: words){
        set.add(word);
     }

     for(int i = 0; i < words.length; i++){
         if(concatenate(words[i], set, 1)){
             result.add(words[i]);
             map.put(words[i],true);
         }  
     }

     return result;   
    }


    public Boolean concatenate(String word, HashSet<String> set, int count){

        if(set.contains(word) && count>1){
            return true;
        }

        if(map.containsKey(word)){
            return map.get(word);
        }

        for(int i=1; i< word.length();i++){
            if(set.contains(word.substring(0,i)) && concatenate(word.substring(i), set, count+1)){
                map.put(word,true);
                return true;
            }
        }
        map.put(word,false);       

        return false;
    }
}

