class Solution {
    class TrieNode {
        public:
        TrieNode* next[26] = {NULL};
        bool isEOW = false;
        TrieNode() {}
    };

    class Trie {
        TrieNode* root;
        public:
        Trie() { root = new TrieNode(); }

		// standard insertion of words into a trie
        void insert(string& word) {
            TrieNode* curr = root;
            for(auto& c: word) {
                int ch = c - 'a';
                if(!curr->next[ch])
                    curr->next[ch] = new TrieNode();
                curr = curr->next[ch];
            }
            curr->isEOW = true;
        }

        bool dfs(string& word, int start, vector<int>& dp, int count) {
            if(start == word.length()) // If we have reached the end of the word, just check if atleast 2 prefix words exist
                return count >= 2;
            if(dp[start] != -1) return dp[start]; //memoization

            TrieNode* curr = root; // start with the root to check if the word[start....i] exists in the word list
            for(int i=start; i<word.length(); i++) {
                int ch = word[i]-'a';
                if(!curr->next[ch]) 
                    return dp[start] = false;
                curr = curr->next[ch];
                if(curr->isEOW && dfs(word, i+1, dp, count+1)) // if we find such a word, check by recursively searching for word[i+1....]
                    return dp[start] = true;
            }
            return dp[start] = false;
        }
    };


public:
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        Trie trie;
        for(auto& word: words)
            trie.insert(word);

        vector<string> res;

        for(auto& word: words) {
            vector<int> dp(word.length(), -1);
            if(trie.dfs(word, 0, dp, 0))
                res.push_back(word);
        }

        return res;
    }
};

