class Solution {
public: 
    struct TrieNode { 
        TrieNode *children[26];
        bool isLeaf; 
        TrieNode(){
            this->isLeaf = false;
            for(int i=0;i<26;i++){
                this->children[i]=NULL;
            }
        }
    }; 


    void insert(TrieNode *p, string w){ 
        int n = w.size(); 

        for (int i = 0; i < n; i++) { 
            int index = w[i]-'a'; 
            if (!p->children[index]) 
                p->children[index] = new TrieNode(); 
            p = p->children[index]; 
        }

        p->isLeaf = true; 
    } 

    vector<string> findAllConcatenatedWordsInADict(vector<string>& words){
        TrieNode *root = new TrieNode();

        for (int i = 0; i < words.size(); i++) 
            insert(root, words[i]);

        vector<string> ans;

        for(auto w : words){
            int n = w.size();
            if(n == 0) continue;
            bool dp[n+1];
            memset(dp,false,sizeof(dp));
            dp[0] = true;

            for(int i=0; i<n; i++){
                if(dp[i] == false) continue;
                TrieNode *p = root;

                for(int j=i;j<n;j++){
                    if (!p->children[w[j]-'a'])break;   
                    p = p->children[w[j]-'a'];
                    if(i==0 && j==n-1)continue; //to skip one words (unconcatenated)
                    if(p->isLeaf)
                        dp[j+1]=true;
                }

                if(dp[n]){
                    ans.push_back(w);
                    break;
                }
            }
        }

        return ans;
    }
};
