    public List<String> findAllConcatenatedWordsInADict(String[] words) {
        List<String> res = new ArrayList<>();
        // make a set to hold all the words
        Set<String> set = new HashSet<>(Arrays.asList(words));
        // try every word by dfs
        for (String word : words) if (dfs(set, word, 0)) res.add(word);
        return res;
    }

    private boolean dfs(Set<String> set, String word, int level) {
        // level must be above 0 if you want to claim this word meet true criteria
        if (set.contains(word) && level != 0) return true;
        for (int i = 1; i < word.length(); i++) {
            // check if prefix exists
            if (!set.contains(word.substring(0, i))) continue;
            // if suffix exists in set, finish and return true, or recursively check the suffix
            String suffix = word.substring(i);
            if (set.contains(suffix) || dfs(set, suffix, level + 1)) return true;
        }
        return false;
    }
