# Code
```
class Solution {
private:
    bool is_present(string &s, vector<string> &words){
        int low=0, high=words.size()-1;
        while(low<=high){
            int mid=(low+high)/2;
            if(words[mid]==s){
                return true;
            }else if(words[mid]<s){
                low=mid+1;
            }else{
                high=mid-1;
            }
        }
        return false;
    }

    bool is_concatenated(string s, vector<string> &words){
        string left="";
        for(int i=0; i<s.size(); i++){
            left+=s[i];
            if(is_present(left,words)){
                string right=s.substr(i+1);
                if(is_present(right,words)) return true;
                if(is_concatenated(right,words)) return true;
            }
        }
        return false;
    }

public:
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        sort(words.begin(),words.end());
        vector<string> ans;
        for(int i=0; i<words.size(); i++){
            string s=words[i];
            if(is_concatenated(s,words)) ans.push_back(s);
        }
        return ans;
    }
};
```

