```
class Solution {
public:
    unordered_map<string,int> mp;
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        for(auto &s : words) mp[s]++;
        vector<string> res;
        for(auto &x : words){
            if(dfs(0,0,0,x)==true) res.push_back(x);
        }
        return res;
    }

    bool dfs(int start,int pos,int c,string &s){
        if(pos>=s.size()){
            if(c>=2 and start==pos) return true;
            return false;
        }

        bool ans = false;
        if(mp.count(s.substr(start,pos-start+1))){
            if(dfs(pos+1,pos+1,c+1,s)==true) ans |= true;
            else if(dfs(start,pos+1,c,s)==true) ans |= true;
        }
        else{
            if(dfs(start,pos+1,c,s)==true) ans |= true;
        }
        return ans;
    }
};
