struct Node{
    bool end = false;
    Node* next[26] = { NULL };
};

class Solution {
public:
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        root = new Node();
        for(const auto& word : words)
            add(word);

        for(const auto& word : words){
            if(dfs(0, 0, word.size(), word))
                ans.push_back(word);
        }
        return ans;
    }

private:
    Node* root;
    vector<string> ans;
    void add(const string& str) {
        auto cur = root;
        for(auto& ch : str) {
            if(!cur->next[ch-'a']) cur->next[ch-'a'] = new Node();
            cur = cur->next[ch-'a'];
        }
        cur->end = true;
    }

    bool dfs(int i, int cnt, int n, const string& s){
        if(i == n) return cnt > 1;
        auto cur = root;
        while(i<n && cur->next[s[i]-'a'] != NULL) {
            cur = cur->next[s[i] - 'a'];
