class Solution {
public:

    // set will store all the words of words

    unordered_set<string> s;

    bool helper(string& str, int i, int n)
    {
        // base case

        if(i == n)
            return true;

        // partition str at j and call for next

        for(int j = i; j < n; j++)
        {
            if(str.substr(i, j - i + 1) != str && s.count(str.substr(i, j - i + 1)))
            {
                if(helper(str, j + 1, n))
                    return true;
            }
        }

        return false;
    }

    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {

        // insert all the word of words into s

        for(auto word : words)
        {
            s.insert(word);
        }

        vector<string> res;

        // iterate over words and check for each word is it concatenated or not

        for(auto word : words)
        { 
            if(helper(word, 0, word.size()))
                res.push_back(word);
        }

        return res;
    }
};
```

