class Solution {
     private static class Node {
		private char data;
		private boolean isEnd;
		private Node[] children;

		public Node(char data) {
			this.data = data;
			this.isEnd = false;
			this.children = new Node[26];
		}
	}

	private Node root = new Node('/');

	private void insert(String word) {
		Node curr = this.root;
		for (int i = 0; i < word.length(); i++) {
			int childIdx = word.charAt(i) - 'a';
			if (curr.children[childIdx] == null) {
				curr.children[childIdx] = new Node(word.charAt(i));
			}
			curr = curr.children[childIdx];
		}
		curr.isEnd = true;
	}

    private boolean isConcatenated(String word, int index, int count) {
        if (index == word.length()){
            return count >= 2;
        }
        Node curr = this.root;
        for (int i = index; i < word.length(); i++) {
            if (curr.children[word.charAt(i) - 'a'] == null){
                 return false;
            } 
            curr = curr.children[word.charAt(i) - 'a'];
            if (curr.isEnd){
                //start searching next part of string again from the root and increment 
                if (isConcatenated(word, i + 1, count + 1)){
                      return true;
                }
            }
        }
        return false;
    }

    public List<String> findAllConcatenatedWordsInADict(String[] words) {
        for (String word : words) {
			insert(word);
		}
        List<String> result = new ArrayList<>();
        for (String word : words) {
            if (isConcatenated(word, 0, 0))
                result.add(word);
        }
        return result;
    }
}
