class Solution:
    def findAllConcatenatedWordsInADict(self, words: List[str]) -> List[str]:
        root, res = {}, set()

        for w in words:
            cur = root
            for c in w: cur = cur.setdefault(c, {})
            cur['#'] = {}

        def dfs(word, l, i, n):
            if i == l:
                if n > 1: res.add(word)
                return
            cur = root
            while i < l:
                if word[i] not in cur: return
                if '#' in cur[word[i]]: dfs(word, l, i + 1, n + 1)
                cur = cur[word[i]]
                i += 1

        for w in words: dfs(w, len(w), 0, 0)
        return list(res)     
