class Solution {
private:
    unordered_set<string> wordSet;

public:
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        wordSet = unordered_set<string>(words.begin(), words.end());
        vector<string> ans;
        for(auto& str : words){
            int count = 0;
            if(isConcat(str, 0, count) == true && count > 1){
                ans.push_back(str);
            }
        }
        return ans;
    }

    bool isConcat(string& str, int index, int& count){
        if(index >= str.size()){
            return true;
        }
        string subStr;
        for(int i = index; i < str.size(); ++i){
            subStr += str[i];
            if(wordSet.find(subStr) != wordSet.end()){
                if(isConcat(str, i + 1, count) == true){       //<- if a substring is found, then dive into next recursive call
                    count++;     //<- this counts the number of substrings present within "str" that are also present within "wordSet" 
                    return true;
                }
            }
        }
        return false;
    }
};
