class Solution:
    def findAllConcatenatedWordsInADict(self, words: List[str]) -> List[str]:
        words = set(words)
        res = []

        for word in words:
            words.remove(word)
            if self.check_word(word, words):
                res.append(word)
            words.add(word)

        return res

    def check_word(self, word, words):

            queue = deque([0])
            visited = set([0])
            while queue:
                ind = queue.popleft()

                for end in range(ind + 1, len(word) + 1):
                    if word[ind:end] in words:
                        if end not in visited:
                            queue.append(end)
                            visited.add(end)
                        if end == len(word):
                            return True
            return False
