    //Structure for TRIE
    struct Node {
        char c; //To store and iterate characters
        int end; //To store the end of a word
        Node* child[26]; //Child array
    };

    //To create a node
    struct Node* getNode(char c) {
        Node* newNode = new Node;
        newNode->c = c;
        newNode->end = 0;
        for(int i=0; i<26; i++) {
            newNode->child[i] = NULL;
        }

        return newNode;
    }

    //Root of the TRIE is created
    Node* root = getNode('/');
    vector<string> v;

    //Function to insert a string into the TRIE
    void insert(string s) {
        Node* curr = root;
        int i = 0, index;
        while(i < s.length()) {
            index = s[i]-'a'; //Index
            if(curr->child[index] == NULL) {
                curr->child[index] = getNode(s[i]); //Create a node if it's empty
            }
            curr = curr->child[index]; //Iterate to next child
            i++;
        }
        curr->end = 1; //Set end to 1 whenever a string's iteration is ended
    }

    //Function to search a string in the TRIE
    bool search(string s) {
        Node* curr = root;
        int i = 0, index;
        while(i < s.length()) {
            index = s[i] -'a';
            if(curr->child[index] == NULL) {
                return false; //Whenever we get empty child for a given string in TRIE, we return false
            }
            curr = curr->child[index]; //Iterate to next child
            i++;
        }

        return curr->end == 1; //If the end is equal to 1 means that there is string present in the TRIE
    }

    //Function to check partitions and substrings
    bool solve(string &s, int cnt) {
        //If we have a valid concatenated word in the trie then return true
        if(cnt > 1 && search(s)) {
            return true;
